#include&lt;iostream&gt;

using namespace std;

class INT {
    friend ostream&amp; operator &lt;&lt; (ostream&amp; os, const INT&amp; i);
public:
    INT(int i) : m_i(i) {};

    INT&amp; operator++() {
        ++(this-&gt;m_i);
        return *this;
    }

    const INT operator++(int) {
        INT temp = *this;
        ++(*this);
        return temp;
    }

    INT&amp; operator--() {
        --(this-&gt;m_i);
        return *this;
    }

    const INT operator--(int) {
        INT temp = *this;
        --(*this);
        return temp;
    }

    int&amp; operator*() const {
        return (int &amp;)m_i;
    }
private:
    int m_i;
};

ostream&amp; operator&lt;&lt;(ostream&amp; os, const INT&amp; i) {
    os &lt;&lt; &#039;{&#039; &lt;&lt; i.m_i &lt;&lt; &#039;}&#039;;
    return os;
}


int main() {
    INT I(5);
    cout &lt;&lt; I++ &lt;&lt; endl;
    cout &lt;&lt; ++I &lt;&lt; endl;
    cout &lt;&lt; I-- &lt;&lt; endl;
    cout &lt;&lt; --I &lt;&lt; endl;
    cout &lt;&lt; *I  &lt;&lt; endl;
}
