#include &lt;iostream&gt;

using std::cout;
using std::endl;

class system {
public:
    virtual void operation() = 0;
};

class systemA : public system {
public:
    void operation() override {
        cout &lt;&lt; &#039;A&#039; &lt;&lt; endl;
    }
};

class systemB : public system {
public:
    void operation() override {
        cout &lt;&lt; &#039;B&#039; &lt;&lt; endl;
    }
};

class systemC : public system {
public:
    void operation() override {
        cout &lt;&lt; &#039;C&#039; &lt;&lt; endl;
    }
};

class Facade {
    systemA *system_a;
    systemB *system_b;
    systemC *system_c;
public:
    explicit Facade() {
        system_a = new systemA();
        system_b = new systemB();
        system_c = new systemC();
    }

    void operation() {
        system_a-&gt;operation();
        system_b-&gt;operation();
        system_c-&gt;operation();
    }

    virtual ~Facade() {
        delete system_a;
        delete system_b;
        delete system_c;
    }
};

int main() {
    (new Facade())-&gt;operation();
}
