#include &lt;iostream&gt;

using std::cout;
using std::endl;

class Subject {
public:
    virtual void request() {
        cout &lt;&lt; &quot;Base&quot; &lt;&lt; endl;
    }
};

class Proxy : public Subject {
    void beforeRequest() {
        cout &lt;&lt; &quot;before&quot; &lt;&lt; endl;
    }

    void afterRequest() {
        cout &lt;&lt; &quot;after&quot; &lt;&lt; endl;
    }

    Subject *subject;

public:
    void request() override {
        beforeRequest();
        subject-&gt;request();
        afterRequest();
    }

    Proxy() {
        subject = new Subject();
    }
};

int main() {
    (new Proxy())-&gt;request();
}
