class Solution {
public:
    vector&lt;int&gt; twoSum(vector&lt;int&gt;&amp; nums, int target) {
        map&lt;int, int&gt; m;
        vector&lt;int&gt; v;
        for(int i = 0; i &lt; nums.size(); ++i) {
            m[nums[i]] = i;
        }
        for(int i = 0; i &lt; nums.size(); ++i) {
            cout &lt;&lt; target - nums[i] &lt;&lt; &#039; &#039; &lt;&lt; m[nums[i]] &lt;&lt;endl;
            if(m.find(target - nums[i]) != m.end() &amp;&amp; m[target - nums[i]] != i) {
                v.push_back(i);
                v.push_back(m[target - nums[i]]);
                break;
            }
        }
        return v;
    }
};
